catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
	if (isLoading || (oldValue == newValue))
		{ return;
}
g_form.hideFieldMsg('unit_number');
var nv = newValue.toUpperCase();

var ga = new GlideAjax('ValidateUnitCharge');
ga.addParam("sysparm_name", "validateUnitCharge");
ga.addParam("sysparm_oldUnit",oldValue);
ga.addParam("sysparm_newUnit",nv);
ga.addParam("sysparm_messageFlag", false);
ga.getXML(ValidateUnitChargeParse);

function ValidateUnitChargeParse(response) {
	var answer = response.responseXML.documentElement.getAttribute("answer");

	if (answer == 'false') {
		g_form.showFieldMsg('unit_number','Unit # ' + newValue + ' is not valid.','error');
		g_form.setValue('unit_number', oldValue);
	}
}
}
