catalog_script_client.script:
function onSubmit() {

	var nu = g_form.getValue('unit_number').toUpperCase();
	var nc = g_form.getValue('charge_number').toUpperCase();

	var ga = new GlideAjax('ValidateUnitCharge');
	ga.addParam("sysparm_name", "validateUnitCharge");
	ga.addParam("sysparm_newCharge",nu);
	ga.addParam("sysparm_newUnit",nc);
	ga.addParam("sysparm_messageFlag", false);
	ga.getXML(ValidateUnitChargeParse);

}

function ValidateUnitChargeParse(response) {
	var answer = response.responseXML.documentElement.getAttribute("answer");
	if (answer == 'false') {
		g_form.showFieldMsg('unit_number','Chrage # ' + nu + ' is not valid.','error');
		g_form.showFieldMsg('charge_number','Unit # ' + nc + ' is not valid.','error');
		g_form.setValue('unit_number', '');
		g_form.setValue('charge_number', '');
	}
}
