// Check for any Reject
var v_state;
if (workflow.scratchpad.approval_1_flag )  {
	if (workflow.scratchpad.approval_1_state == 'Accepted'){
		v_state = 'Accept';
	}
	if (workflow.scratchpad.approval_1_state == 'Rejected'){
		v_state = 'Reject';
	}
}
if (workflow.scratchpad.approval_2_flag )  {
	if (workflow.scratchpad.approval_2_state == 'Accepted'){
		v_state = 'Accept';
	}
	if (workflow.scratchpad.approval_2_state == 'Rejected'){
		v_state = 'Reject';
	}
}
switch (v_state.toString().trim()) {
	case 'Accept':
	current.state = '3';
	break;
	case 'Reject':
	current.state = '7';
	break;
}


// Check for open Items attached to Request

var item = new GlideRecord('sc_req_item');
item.addQuery('request', current.request);
item.addQuery('active', false);
item.query();
if (!item.next()) {
	// No Open Items Found, close Request
	//gs.log("Closing Request" + current.request.number );
	var req = new GlideRecord('sc_request');
	req.addQuery('sys_id', current.request);
	req.query();
	while (req.next()){
		if (v_state == 'Reject') {
			req.request_state = 'closed_cancelled';
			//req.comments.setJournalEntry("Request Automatically Closed Canceled.");
		}
		if (v_state == 'Accept') {
			req.request_state = 'closed_complete';
			//req.comments.setJournalEntry("Request Automatically Closed Completed.");
		}
		req.active = false;
		req.update();
	}
}
else {
	gs.info('Not Closing Request, items found');
}

// Check for any Reject
var v_state;
if (workflow.scratchpad.approval_1_flag )  {
	if (workflow.scratchpad.approval_1_state == 'Accepted'){
		v_state = 'Accept';
	}
	if (workflow.scratchpad.approval_1_state == 'Rejected'){
