createCI();

function createCI(){
	
	var grCI = new GlideRecord('x_arps_certificate_management');
	grCI.initialize();
	
	// Direct and assignments from variable pool
	grCI.name = current.variable_pool.cert_mgmt_name_new;
	grCI.asset_tag = current.variable_pool.cert_mgmt_name_new;
	grCI.u_service_level = current.variable_pool.cert_mgmt_service_level;
	grCI.u_managed = true;
	grCI.install_status = 300; // In-Service
	grCI.u_lifecycle = 2; // Active
	grCI.u_usage = 2; // Production
	grCI.cert_validation_group = current.variable_pool.cert_mgmt_cert_valid_group;
	grCI.cert_install_group = current.variable_pool.cert_mgmt_cert_install_group;
	grCI.automatic_renewal = false;
	grCI.valid_from = current.variable_pool.cert_mgmt_valid_from;
	grCI.valid_to = current.variable_pool.cert_mgmt_valid_to;
	grCI.u_charge_number = current.variable_pool.charge_number;
	grCI.u_department_number = current.variable_pool.unit_number;
	
	// Lookups
	// CI Category
	var gr_ci_cat = new GlideRecord('u_ci_category');
	gr_ci_cat.addQuery('u_categorization', 'Digital Certificate');
	gr_ci_cat.query();
	while (gr_ci_cat.next()){
		grCI.u_ci_category = gr_ci_cat.sys_id;
	}
	// Owned by & Support Group
	var gr_Group = new GlideRecord('sys_user_group');
	gr_Group.addQuery('name', 'IS SECURITY OPS - CERTIFICATES');
	gr_Group.query();
	while (gr_Group.next()){
		grCI.owned_by = gr_Group.manager;
		grCI.support_group = gr_Group.sys_id;
	}
	
	// Approval Group
	var gr_AGroup = new GlideRecord('sys_user_group');
	gr_AGroup.addQuery('name', 'IS SECURITY OPS - APPROVAL');
	gr_AGroup.query();
	while (gr_AGroup.next()){
		//grCI.owned_by = gr_Group.manager;
		grCI.change_control = gr_AGroup.sys_id;
	}
	
	grCI.insert();
}

