function ifScript() {
	//	var v_ReqFor = current.variable_pool.cert_mgmt_caller_id;
	//	var v_ValGroup = current.variable_pool.cert_mgmt_cert_valid_group.toString();
	
	var v_ReqFor= current.variable_pool.cert_mgmt_caller_id;
	var v_ValGroup = current.variable_pool.cert_mgmt_cert_valid_group;
	
	//	gs.info('100 - v_ReqFor = ' + v_ReqFor);
	//	gs.info('200 - v_ValGroup = ' + v_ValGroup);
	
	
	
	if (v_ReqFor!== '') {
		var gr = new GlideRecord("sys_user_grmember");
		gr.addQuery("user", v_ReqFor);
		gr.addQuery("group", v_ValGroup);
		gr.query();
		if (gr.next()) {
			//			gs.info('300 - Answer = Yes');
			return  'yes';
		}
		//		gs.info('400 - Answer = No');
		return 'no';
	}
}

