var gr = new GlideRecord('x_arps_certificate_management');
gr.addQuery("sys_id", current.variable_pool.cert_mgmt_name);
gr.query();
if (gr.next()) {
	current.variable_pool.cert_mgmt_cert_valid_group = gr.cert_validation_group;
	current.variable_pool.cert_mgmt_cert_install_group = gr.cert_install_group;
	//	gs.info('333 - cert Validation group = ' + current.variable_pool.cert_mgmt_cert_valid_group);
	//	gs.info('334 - cert Installation group = ' + current.variable_pool.cert_mgmt_cert_install_group);
}

