switch  (current.variable_pool.cert_mgmt_request_type.toString().trim()){
	case 'New':
	current.short_description = current.short_description + ' - New Certificate';
	break;
	case 'Renewal':
	current.short_description = current.short_description + ' - Renewal Certificate';
	break;
	case 'Replace':
	current.short_description = current.short_description + ' - Replace Certificate';
	break;
	case 'Revoke':
	current.short_description = current.short_description + ' - Revoke Certificate';
	break;
}

